// Solicitar característica premium con x402
const response = await fetch('/api/x402/analytics', {
  headers: {
    'X-Payment-Token': 'tu-token-de-pago',
    'X-Payment-Amount': '0.001' // costo en moneda
  }
});

if (response.status === 402) {
  // Pago requerido - mostrar UI de pago
  console.log('La característica premium requiere pago');
} else if (response.ok) {
  // Pago aceptado, característica desbloqueada
  const data = await response.json();
}
